import React from 'react'
import styled from 'styled-components'
import { Link } from 'react-router-dom'
// import heroBcg from '../assets/hero-bcg.jpeg'
// import heroBcg2 from '../assets/hero-bcg-2.jpeg'

const Hero = () => {
  return (
    <Wrapper className='section-center'>
      <article className='content'>
        <h1>
         Bonjour?! Welcome To LetsBuyShop <br />         
        </h1>
        <small>Shop with Quality, And Elegance</small>
        <p>
          We are committed to giving you the best at your convinience.
          Just browse ,click and own.
        </p>
        <Link to='/products' className='btn hero-btn'>
          Start Shopping Now! &#8658;
        </Link>
      </article>
      <article className='img-container'>
        <img src='https://ke.jumia.is/unsafe/fit-in/680x680/filters:fill(white)/product/59/786019/5.jpg?4596' alt='PlayStation' className='main-img' />
        <img src='https://ke.jumia.is/unsafe/fit-in/680x680/filters:fill(white)/product/20/253217/1.jpg?8012' alt='Cotton ' className='accent-img' />
      </article>
    </Wrapper>
  )
}

const Wrapper = styled.section`
  min-height: 60vh;
  display: grid;
  place-items: center;
  .img-container {
    display: none;
  }

  p {
    line-height: 2;
    max-width: 45em;
    margin-bottom: 2rem;
    color: var(--clr-grey-5);
    font-size: 1rem;
  }
  @media (min-width: 992px) {
    height: calc(100vh - 5rem);
    grid-template-columns: 1fr 1fr;
    gap: 8rem;
    h1 {
      margin-bottom: 2rem;
    }
    p {
      font-size: 1.25rem;
    }
    .hero-btn {
      padding: 0.75rem 1.5rem;
      font-size: 1rem;
    }
    .img-container {
      display: block;
      position: relative;
    }
    .main-img {
      width: 100%;
      height: 550px;
      position: relative;
      border-radius: var(--radius);
      display: block;
      object-fit: cover;
    }
    .accent-img {
      position: absolute;
      bottom: 0;
      left: 0;
      width: 250px;
      transform: translateX(-50%);
      border-radius: var(--radius);
    }
    .img-container::before {
      content: '';
      position: absolute;
      width: 10%;
      height: 80%;
      background: var(--clr-primary-9);
      bottom: 0%;
      left: -8%;
      border-radius: var(--radius);
    }
  }
`

export default Hero
